	<hr/>    
	<div class = "panel panel-default">  
	    <div class = "panel-heading"> 
	        <h4 class = "panel-title"> 
	            <a href = "#collapseOne" data-parent = "#accordion" data-toggle = "collapse" class = "accordion-toggle">Duration</a>
